import React, { useState, useEffect } from "react";
import iotLogo from "../designFIles/IoT_logo_transparent_bigger.png";
import "./welcomeBody.css";
import { Chart } from "react-charts";

// Function to calculate average minutes on every hour
const calculateAverageMinutes = (selectedData) => {
  const hoursData = Array.from({ length: 24 }, () => []);
  selectedData.forEach((row) => {
    if (row && row.length >= 3) {
      const [_, timeOfDay, timeOnSeconds] = row;
      const hour = parseInt(timeOfDay.split(":")[0]);
      hoursData[hour].push(timeOnSeconds / 60); // Converting seconds to minutes
    }
  });

  const averageMinutesOnHour = hoursData.map((hourData) => {
    const sum = hourData.reduce((acc, cur) => acc + cur, 0);
    return hourData.length > 0 ? sum / hourData.length : 0;
  });

  return averageMinutesOnHour;
};

export const WelcomeBody = () => {
  const [showVisualMenuSelected, setShowVisualMenuSelected] = useState("table");
  const [tableData, setTableData] = useState([]);
  const [sortDirection, setSortDirection] = useState("asc");
  const [selectedDay, setSelectedDay] = useState(""); // State to hold the selected day

  const handleLogout = () => {
    const confirmLogout = window.confirm("Are you sure you want to log out?");
    if (confirmLogout) {
      sessionStorage.clear();
      fetch("/logout", {
        method: "POST",
        headers: {
          "Cache-Control": "no-cache, no-store, must-revalidate",
          Pragma: "no-cache",
          "Content-Type": "application/json",
        },
        credentials: "same-origin",
      })
        .then(() => {
          const baseURL = window.location.origin;
          window.location.href = `${baseURL}/login`;
        })
        .catch((error) => {
          console.error("Error logging out:", error);
        });
    }
  };

  useEffect(() => {
    const fileURL =
      "https://firebasestorage.googleapis.com/v0/b/iot-light-68724.appspot.com/o/bulbOnTime.txt?alt=media&token=74cb4268-f94e-46da-8e54-83489ab34a80";

    fetch(fileURL)
      .then((response) => response.text())
      .then((data) => {
        const rows = data.split("\n").map((row) => {
          const [date, timeOfDay, timeOnSeconds] = row.split("\t");
          return [date, timeOfDay, parseInt(timeOnSeconds)];
        });
        const sortedData = rows.sort((a, b) =>
          sortDirection === "asc"
            ? new Date(a[0]) - new Date(b[0])
            : new Date(b[0]) - new Date(a[0])
        );
        setTableData(sortedData);
      })
      .catch((error) => {
        console.error("Error fetching file:", error);
      });
  }, [sortDirection]);

  // Function to handle day selection
  const handleDaySelect = (day) => {
    setSelectedDay(day);
  };

  return (
    <div
      style={{
        backgroundColor: "#414141",
        textAlign: "center",
      }}
    >
      <div className="header-style">
        <div className="nav">
          <div className="left-nav-text">Eco Light</div>
          <img
            className="header-logo"
            src={iotLogo}
            alt="IoT Logo"
            style={{ width: "5rem", height: "auto", paddingLeft: "0.5rem" }}
          />
          <div className="right-nav-text" onClick={handleLogout}>
            Log Out
          </div>
        </div>
      </div>
      <div className="options-layout">
        <div className="parent">
          <a
            className={
              showVisualMenuSelected === "table" ? "clicked-option" : "child"
            }
            onClick={() => setShowVisualMenuSelected("table")}
            style={{
              display: "inline-block",
              padding: "0.5rem 1rem",
              margin: "0.5rem",
              cursor: "pointer",
              color: showVisualMenuSelected === "table" ? "#414141" : "#fff",
              fontWeight: "bold",
              fontSize: "2.5rem",
              border: "0.2rem solid #ffffff",
              borderRadius: "0.45rem",
              backgroundColor:
                showVisualMenuSelected === "table" ? "#ffffff" : "transparent",
              transition: "background-color 0.3s, color 0.3s",
            }}
          >
            Table
          </a>
          <a
            className={
              showVisualMenuSelected === "graph" ? "clicked-option" : "child"
            }
            onClick={() => setShowVisualMenuSelected("graph")}
            style={{
              display: "inline-block",
              padding: "0.5rem 1rem",
              margin: "0.5rem",
              cursor: "pointer",
              color: showVisualMenuSelected === "graph" ? "#414141" : "#fff",
              fontWeight: "bold",
              fontSize: "2.5rem",
              border: "0.2rem solid #ffffff",
              borderRadius: "0.45rem",
              backgroundColor:
                showVisualMenuSelected === "graph" ? "#ffffff" : "transparent",
              transition: "background-color 0.3s, color 0.3s",
            }}
          >
            Graph
          </a>
        </div>
        {/* Button to select the day */}
        {showVisualMenuSelected === "graph" && (
          <div>
            <button onClick={() => handleDaySelect("")}>All Days</button>
            {tableData.map((row, index) => (
              <button key={index} onClick={() => handleDaySelect(row[0])}>
                {row[0]}
              </button>
            ))}
          </div>
        )}
      </div>
      {showVisualMenuSelected === "table" ? (
        <div
          style={{
            margin: "0 auto",
            maxWidth: "800px",
            backgroundColor: "white",
          }}
        >
          {/* Table rendering code */}
        </div>
      ) : (
        <div>
          {/* Graph rendering code */}
          <div
            style={{
              width: "90%",
              height: "345px",
              backgroundColor: "#fff",
              margin: "0 auto",
            }}
          >
            <Chart
              data={[
                {
                  label: "Average Time On (minutes)",
                  data: calculateAverageMinutes(
                    selectedDay
                      ? tableData.filter((row) => row[0] === selectedDay)
                      : tableData
                  ).map((value, index) => [index, value]),
                },
              ]}
              axes={[
                { primary: true, type: "linear", position: "bottom" },
                { type: "linear", position: "left" },
              ]}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default WelcomeBody;
