import React, { useState, useEffect } from "react";
import iotLogo from "../designFIles/IoT_logo_transparent_bigger.png";
import "./welcomeBody.css";

export const WelcomeBody = () => {
  const [showVisualMenuSelected, setShowVisualMenuSelected] = useState("table");
  const [tableData, setTableData] = useState([]);

  const handleLogout = () => {
    const confirmLogout = window.confirm("Are you sure you want to log out?");
    if (confirmLogout) {
      sessionStorage.clear();

      fetch("/logout", {
        method: "POST",
        headers: {
          "Cache-Control": "no-cache, no-store, must-revalidate",
          Pragma: "no-cache",
          "Content-Type": "application/json",
        },
        credentials: "same-origin",
      })
        .then(() => {
          const baseURL = window.location.origin;
          window.location.href = `${baseURL}/login`;
        })
        .catch((error) => {
          console.error("Error logging out:", error);
        });
    }
  };

  useEffect(() => {
    const fileURL =
      "https://firebasestorage.googleapis.com/v0/b/iot-light-68724.appspot.com/o/bulbOnTime.txt?alt=media&token=74cb4268-f94e-46da-8e54-83489ab34a80";

    fetch(fileURL)
      .then((response) => response.text())
      .then((data) => {
        // Parse the fetched data into an array of arrays
        const rows = data.split("\n").map((row) => row.split(","));
        setTableData(rows);
      })
      .catch((error) => {
        console.error("Error fetching file:", error);
        // Handle error
      });
  }, []);

  return (
    <div>
      <div className="header-style">
        <div className="nav">
          <div className="left-nav-text">Eco Light</div>
          <img
            className="header-logo"
            src={iotLogo}
            alt="IoT Logo"
            style={{ width: "5rem", height: "auto", paddingLeft: "0.5rem" }}
          />
          <div className="right-nav-text" onClick={handleLogout}>
            Log Out
          </div>
        </div>
      </div>
      <div className="options-layout">
        <div className="parent">
          <a
            className={
              showVisualMenuSelected === "Table" ? "clicked-option" : "child"
            }
            onClick={() => setShowVisualMenuSelected("table")}
          >
            Table
          </a>
          <a
            className={
              showVisualMenuSelected === "Graph" ? "clicked-option" : "child"
            }
            onClick={() => setShowVisualMenuSelected("analytics")}
          >
            Graph
          </a>{" "}
        </div>
      </div>
      <div style={{ marginTop: "20px" }}>
        <h2>Table</h2>
        <table
          style={{
            fontFamily: "Arial, sans-serif",
            borderCollapse: "collapse",
            width: "100%",
          }}
        >
          <tbody>
            {tableData.map((rowData, rowIndex) => {
              // Ensure rowData is a string before splitting
              if (typeof rowData === "string") {
                // Split each row by the hyphen to separate data and time
                const [data, time] = rowData.split(" - ");
                // Split the time part by the colon to separate hour and seconds
                const [hour, seconds] = time.split(":");

                return (
                  <tr key={rowIndex}>
                    <td
                      style={{
                        border: "1px solid #dddddd",
                        textAlign: "left",
                        padding: "8px",
                      }}
                    >
                      {data}
                    </td>
                    <td
                      style={{
                        border: "1px solid #dddddd",
                        textAlign: "left",
                        padding: "8px",
                      }}
                    >
                      Hour: {hour}
                    </td>
                    <td
                      style={{
                        border: "1px solid #dddddd",
                        textAlign: "left",
                        padding: "8px",
                      }}
                    >
                      Seconds: {seconds}
                    </td>
                  </tr>
                );
              } else {
                return null; // Handle invalid rowData
              }
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};
