import React from "react";
import logo from "../designFIles/EcoLight.png";
import googleLoginImage from "../designFIles/googlelogin.png";
import "./logInIndex.css";
import { signInWithGooglePopup } from "./firebase";
import { v4 as uuidv4 } from "uuid"; // Import uuid library

export const LogInIndex = () => {
  const logGoogleUser = async () => {
    try {
      const response = await signInWithGooglePopup();
      console.log(response);
      // Generate a unique token using uuid
      const token = uuidv4();
      // Redirect to the welcome page with the token included in the URL
      window.location.href = `http://localhost:3000/welcome?token=${token}`;
    } catch (error) {
      console.error("Error signing in with Google:", error.message);
    }
  };

  return (
    <div className="container">
      <img
        className="logo"
        src={logo}
        alt="Logo"
        style={{ width: "25rem", height: "auto" }}
      />
      <img
        id="google-login-btn"
        src={googleLoginImage}
        alt="Google Login"
        className="google-login-button"
        onClick={(e) => {
          e.preventDefault();
          e.stopPropagation();
          logGoogleUser();
        }}
      />
    </div>
  );
};

export default LogInIndex;
