import React, { useState, useEffect } from "react";
import iotLogo from "../designFIles/IoT_logo_transparent_bigger.png";
import "./welcomeBody.css";
import { Chart } from "react-charts";

export const WelcomeBody = () => {
  const [showVisualMenuSelected, setShowVisualMenuSelected] = useState("table");
  const [tableData, setTableData] = useState([]);
  const [sortDirection, setSortDirection] = useState("asc");
  const [chartData, setChartData] = useState([]);

  const handleLogout = () => {
    const confirmLogout = window.confirm("Are you sure you want to log out?");
    if (confirmLogout) {
      sessionStorage.clear();
      fetch("/logout", {
        method: "POST",
        headers: {
          "Cache-Control": "no-cache, no-store, must-revalidate",
          Pragma: "no-cache",
          "Content-Type": "application/json",
        },
        credentials: "same-origin",
      })
        .then(() => {
          const baseURL = window.location.origin;
          window.location.href = `${baseURL}/login`;
        })
        .catch((error) => {
          console.error("Error logging out:", error);
        });
    }
  };

  useEffect(() => {
    const fileURL =
      "https://firebasestorage.googleapis.com/v0/b/iot-light-68724.appspot.com/o/bulbOnTime.txt?alt=media&token=74cb4268-f94e-46da-8e54-83489ab34a80";

    fetch(fileURL)
      .then((response) => response.text())
      .then((data) => {
        const rows = data.split("\n").map((row) => {
          const [dateTime, timeOnSeconds] = row.split(" - Time On: ");
          const [date, timeOfDay] = dateTime.split(" ");
          const formattedTimeOfDay = formatTimeOfDay(timeOfDay);
          const formattedTimeOn = timeOnSeconds
            ? timeOnSeconds.split(" ")[0]
            : timeOnSeconds;
          return [
            new Date(date),
            formattedTimeOfDay,
            parseInt(formattedTimeOn),
          ];
        });

        const groupedData = rows.reduce((acc, [date, timeOfDay, timeOn]) => {
          const key = date.toDateString(); // Grouping by day
          if (!acc[key]) {
            acc[key] = { day: key, totalSeconds: 0 };
          }
          acc[key].totalSeconds += timeOn;
          return acc;
        }, {});

        const chartData = Object.values(groupedData).map(
          ({ day, totalSeconds }) => {
            const hours = totalSeconds / 3600; // Convert seconds to hours
            return [new Date(day), hours];
          }
        );

        const sortedChartData = chartData.sort((a, b) =>
          sortDirection === "asc" ? a[0] - b[0] : b[0] - a[0]
        );

        const sortedData = rows.sort((a, b) =>
          sortDirection === "asc" ? a[0] - b[0] : b[0] - a[0]
        );

        setTableData(sortedData);
        setChartData(sortedChartData);
      })
      .catch((error) => {
        console.error("Error fetching file:", error);
      });
  }, [sortDirection]);

  const formatTimeOfDay = (timeOfDay) => {
    return timeOfDay;
  };

  const handleSort = () => {
    setSortDirection((prevDirection) =>
      prevDirection === "asc" ? "desc" : "asc"
    );
  };

  return (
    <div
      style={{
        backgroundColor: "#414141",
        textAlign: "center",
      }}
    >
      <div className="header-style">
        <div className="nav">
          <div className="left-nav-text">Eco Light</div>
          <img
            className="header-logo"
            src={iotLogo}
            alt="IoT Logo"
            style={{ width: "5rem", height: "auto", paddingLeft: "0.5rem" }}
          />
          <div className="right-nav-text" onClick={handleLogout}>
            Log Out
          </div>
        </div>
      </div>
      <div className="options-layout">
        <div className="parent">
          <a
            className={
              showVisualMenuSelected === "table" ? "clicked-option" : "child"
            }
            onClick={() => setShowVisualMenuSelected("table")}
            style={{
              display: "inline-block",
              padding: "0.5rem 1rem",
              margin: "0.5rem",
              cursor: "pointer",
              color: showVisualMenuSelected === "table" ? "#414141" : "#fff",
              fontWeight: "bold",
              fontSize: "2.5rem",
              border: "0.2rem solid #ffffff",
              borderRadius: "0.45rem",
              backgroundColor:
                showVisualMenuSelected === "table" ? "#ffffff" : "transparent",
              transition: "background-color 0.3s, color 0.3s",
            }}
          >
            Table
          </a>
          <a
            className={
              showVisualMenuSelected === "graph" ? "clicked-option" : "child"
            }
            onClick={() => setShowVisualMenuSelected("graph")}
            style={{
              display: "inline-block",
              padding: "0.5rem 1rem",
              margin: "0.5rem",
              cursor: "pointer",
              color: showVisualMenuSelected === "graph" ? "#414141" : "#fff",
              fontWeight: "bold",
              fontSize: "2.5rem",
              border: "0.2rem solid #ffffff",
              borderRadius: "0.45rem",
              backgroundColor:
                showVisualMenuSelected === "graph" ? "#ffffff" : "transparent",
              transition: "background-color 0.3s, color 0.3s",
            }}
          >
            Graph
          </a>
        </div>
      </div>
      {showVisualMenuSelected === "table" ? (
        <div
          style={{
            margin: "0 auto",
            maxWidth: "800px",
            backgroundColor: "white",
          }}
        >
          <table style={{ width: "100%", borderCollapse: "collapse" }}>
            <thead>
              <tr>
                <th
                  style={{
                    border: "1px solid #ddd",
                    padding: "8px",
                    cursor: "pointer",
                  }}
                  onClick={handleSort}
                >
                  Date {sortDirection === "asc" ? "▲" : "▼"}
                </th>
                <th style={{ border: "1px solid #ddd", padding: "8px" }}>
                  Time of Day
                </th>
                <th style={{ border: "1px solid #ddd", padding: "8px" }}>
                  Time On (seconds)
                </th>
              </tr>
            </thead>
            <tbody>
              {tableData.map((row, index) => (
                <tr key={index}>
                  <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                    {row[0]}
                  </td>
                  <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                    {row[1]}
                  </td>
                  <td style={{ border: "1px solid #ddd", padding: "8px" }}>
                    {row[2]}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <div>
          <div
            style={{
              // width: "1000px",
              // height: "350px",
              width: "90%",
              height: "345px",
              backgroundColor: "#fff",
              margin: "0 auto",
            }}
          >
            <Chart
              data={[
                {
                  label: "Hours On",
                  data: chartData,
                },
              ]}
              axes={[
                { primary: true, type: "time", position: "bottom" },
                { type: "linear", position: "left" },
              ]}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default WelcomeBody;
