import React, { useState, useEffect } from "react";
import firebase from "firebase/compat/app";
import "firebase/storage";
import logo from "../designFIles/EcoLight.png";
import googleLoginImage from "../designFIles/googlelogin.png";
import "./logInIndex.css";
import { signInWithGooglePopup } from "./firebase";
import { v4 as uuidv4 } from "uuid";

export const LogInIndex = () => {
  const [bulbOnTime, setBulbOnTime] = useState(null);
  const [fetchError, setFetchError] = useState(null); // State for fetch error handling

  const logGoogleUser = async () => {
    try {
      sessionStorage.clear();
      const response = await signInWithGooglePopup();
      console.log(response);
      const token = uuidv4();
      const baseURL = window.location.origin;
      window.location.href = `${baseURL}/welcome?token=${token}`;
    } catch (error) {
      console.error("Error signing in with Google:", error.message);
    }
  };

  useEffect(() => {
    const fetchBulbOnTime = async () => {
      try {
        const response = await fetch(
          "https://firebasestorage.googleapis.com/v0/b/iot-light-68724.appspot.com/o/bulbOnTime.txt?alt=media",
          {
            mode: "no-cors", // Potential CORS issue workaround (temporary)
            headers: {
              "Content-Type": "text/plain",
            },
          }
        );

        if (!response.ok) {
          // Check for non-2xx status codes
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.text();
        setBulbOnTime(data);
        console.log("CEVA" + data);
      } catch (error) {
        console.error("Error fetching bulbOnTime.txt:", error.message);
        setFetchError(error.message); // Set fetch error state
      }
    };

    fetchBulbOnTime();
    window.history.replaceState({}, document.title, window.location.pathname);
  }, []);

  return (
    <div className="container">
      <img
        className="logo"
        src={logo}
        alt="Logo"
        style={{ width: "25rem", height: "auto" }}
      />
      <img
        id="google-login-btn"
        src={googleLoginImage}
        alt="Google Login"
        className="google-login-button"
        onClick={(e) => {
          e.preventDefault();
          e.stopPropagation();
          logGoogleUser();
        }}
      />
      {fetchError && <div>Error fetching bulbOnTime: {fetchError}</div>}{" "}
      {/* Display error if any */}
    </div>
  );
};
