import React from "react";
import logo from "../designFIles/EcoLight.png";
import googleLoginImage from "../designFIles/googlelogin.png";
import "./logInIndex.css";
import {
  signInWithGooglePopup,
  getStorage,
  ref,
  getDownloadURL,
} from "./firebase"; // Assuming you have firebase.js with necessary functions
import { v4 as uuidv4 } from "uuid";

export const LogInIndex = () => {
  const logGoogleUser = async () => {
    try {
      const response = await signInWithGooglePopup();
      console.log(response);
      const storage = getStorage(); // Initialize Firebase Storage
      const fileRef = ref(storage, "file.txt"); // Reference to your file.txt
      const fileUrl = await getDownloadURL(fileRef); // Get download URL of the file
      const fileData = await fetch(fileUrl); // Fetch the file using its URL
      const text = await fileData.text(); // Get text content of the file
      console.log(text); // Log the text content of the file
      const token = uuidv4();
      const baseURL = window.location.origin;
      window.location.href = `${baseURL}/welcome?token=${token}`;
    } catch (error) {
      console.error("Error signing in with Google:", error.message);
    }
  };

  React.useEffect(() => {
    window.history.replaceState({}, document.title, window.location.pathname);
  }, []);

  return (
    <div className="container">
      <img
        className="logo"
        src={logo}
        alt="Logo"
        style={{ width: "25rem", height: "auto" }}
      />
      <img
        id="google-login-btn"
        src={googleLoginImage}
        alt="Google Login"
        className="google-login-button"
        onClick={(e) => {
          e.preventDefault();
          e.stopPropagation();
          logGoogleUser();
        }}
      />
    </div>
  );
};
