import React from "react";
import { initializeApp, getAuth, GoogleAuthProvider } from "firebase/app";
import "firebase/auth"; // Optional for older syntax
import logo from "../designFIles/IoT_Light.jpg";
import googleLoginImage from "../designFIles/googlelogin.png";
import "./logInIndex.css"; // Assuming you have CSS styles

const firebaseConfig = {
  // Replace with your actual Firebase project configuration
  apiKey: "YOUR_API_KEY",
  authDomain: "your-project-id.firebaseapp.com",
  projectId: "your-project-id",
  storageBucket: "your-project-id.appspot.com",
  messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
  appId: "YOUR_APP_ID",
};

// Initialize Firebase app (consider moving this to a separate file)
const firebaseApp = initializeApp(firebaseConfig);

// Get Auth instance
const firebaseAuth = getAuth(firebaseApp);

// Create GoogleAuthProvider
const provider = new GoogleAuthProvider();

const handleGoogleSignIn = () => {
  firebaseAuth
    .signInWithPopup(provider)
    .then((result) => {
      console.log("Successful login:", result.user);
      // You can potentially redirect or handle user data here
    })
    .catch((error) => {
      console.error("Login error:", error);
    });
};

const LogInIndex = () => {
  return (
    <div className="container">
      <img
        className="logo"
        src={logo}
        alt="Logo"
        style={{ width: "25rem", height: "auto" }}
      />
      <img
        src={googleLoginImage}
        alt="Google Login"
        className="google-login-button"
        onClick={handleGoogleSignIn}
      />
    </div>
  );
};

export { LogInIndex }; // Named export
