import React from "react";
import logo from "../designFIles/IoT_Light.jpg";
import googleLoginImage from "../designFIles/googlelogin.png";
import "./logInIndex.css";
import SignIn from "./SignIn"; // Corrected import statement
import { signInWithGooglePopup } from "./firebase"; // Import signInWithGooglePopup function

export const LogInIndex = () => {
  const logGoogleUser = async () => {
    const response = await signInWithGooglePopup();
    console.log(response);
  };

  return (
    <div className="container">
      <img
        className="logo"
        src={logo}
        alt="Logo"
        style={{ width: "25rem", height: "auto" }}
      />
      <img
        id="google-login=btn"
        src={googleLoginImage}
        alt="Google Login"
        className="google-login-button"
        onClick={(e) => {
          e.preventDefault();
          e.stopPropagation();
          logGoogleUser();
        }}
      />
    </div>
  );
};

export default LogInIndex;

