import React, { useState, useEffect } from "react";
import iotLogo from "../designFIles/IoT_logo_transparent_bigger.png";
import "./welcomeBody.css";

export const WelcomeBody = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const isLoggedInValue = localStorage.getItem("isLoggedIn");
    setIsLoggedIn(!!isLoggedInValue); // Convert to boolean
  }, []);

  const handleLogout = () => {
    const confirmLogout = window.confirm("Are you sure you want to log out?");
    if (confirmLogout) {
      localStorage.removeItem("isLoggedIn"); // Remove authentication flag
      window.location.href = "http://localhost:3000/login";
    }
  };

  if (!isLoggedIn) {
    window.location.href = "http://localhost:3000/login";
    return null; // Render nothing while redirecting
  }

  return (
    <div>
      <div className="header-style">
        <div className="nav">
          <div className="left-nav-text">Eco Light</div>
          <img
            className="header-logo"
            src={iotLogo}
            alt="IoT Logo"
            style={{ width: "5rem", height: "auto", paddingLeft: "0.5rem" }}
          />
          <div className="right-nav-text" onClick={handleLogout}>
            Log Out
          </div>
        </div>
      </div>
      <div className="options-layout">
        <div className="parent">
          <a className="child">Table</a>
          <a className="child">Graph</a>
        </div>
      </div>
    </div>
  );
};
