import React, { useState, useEffect } from "react";
import iotLogo from "../designFIles/IoT_logo_transparent_bigger.png";
import "./welcomeBody.css";
import { Chart } from "react-charts";

export const WelcomeBody = () => {
  const [showVisualMenuSelected, setShowVisualMenuSelected] = useState("table");
  const [tableData, setTableData] = useState([]);
  const [sortDirection, setSortDirection] = useState("asc");
  const [selectedDate, setSelectedDate] = useState(null);

  const handleLogout = () => {
    const confirmLogout = window.confirm("Are you sure you want to log out?");
    if (confirmLogout) {
      sessionStorage.clear();
      fetch("/logout", {
        method: "POST",
        headers: {
          "Cache-Control": "no-cache, no-store, must-revalidate",
          Pragma: "no-cache",
          "Content-Type": "application/json",
        },
        credentials: "same-origin",
      })
        .then(() => {
          const baseURL = window.location.origin;
          window.location.href = `${baseURL}/login`;
        })
        .catch((error) => {
          console.error("Error logging out:", error);
        });
    }
  };

  useEffect(() => {
    const fileURL =
      "https://firebasestorage.googleapis.com/v0/b/iot-light-68724.appspot.com/o/bulbOnTime.txt?alt=media&token=74cb4268-f94e-46da-8e54-83489ab34a80";

    fetch(fileURL)
      .then((response) => response.text())
      .then((data) => {
        const rows = data.split("\n").map((row) => {
          const [dateTime, timeOnSeconds] = row.split(" - Time On: ");
          const [date, timeOfDay] = dateTime.split(" ");
          const formattedTimeOfDay = formatTimeOfDay(timeOfDay);
          const formattedTimeOn = timeOnSeconds
            ? timeOnSeconds.split(" ")[0]
            : timeOnSeconds;
          return [date, formattedTimeOfDay, parseInt(formattedTimeOn)];
        });
        const sortedData = rows.sort((a, b) =>
          sortDirection === "asc"
            ? new Date(a[0]) - new Date(b[0])
            : new Date(b[0]) - new Date(a[0])
        );
        setTableData(sortedData);
      })
      .catch((error) => {
        console.error("Error fetching file:", error);
      });
  }, [sortDirection]);

  const formatTimeOfDay = (timeOfDay) => {
    return timeOfDay;
  };

  const handleSort = () => {
    setSortDirection((prevDirection) =>
      prevDirection === "asc" ? "desc" : "asc"
    );
  };

  const handleDaySelect = (date) => {
    setSelectedDate(date);
  };

  const generateChartData = () => {
    if (!selectedDate) return [];

    const selectedDayData = tableData.filter((row) => row[0] === selectedDate);
    const hourMap = new Array(24).fill(0);
    const hourCount = new Array(24).fill(0);

    selectedDayData.forEach((row) => {
      const hour = parseInt(row[1].split(":")[0]);
      hourMap[hour] += row[2];
      hourCount[hour]++;
    });

    const averageMinutesPerHour = hourMap.map((totalMinutes, index) => {
      const count = hourCount[index];
      return count > 0 ? Math.round(totalMinutes / count / 60) : 0;
    });

    return averageMinutesPerHour.map((average, index) => [index, average]);
  };

  return (
    <div style={{ backgroundColor: "#414141", textAlign: "center" }}>
      <div className="header-style">{/* Header content */}</div>
      <div className="options-layout">{/* Options layout content */}</div>
      {showVisualMenuSelected === "graph" && (
        <div>
          <div>
            {selectedDate && (
              <div>
                <button onClick={() => handleDaySelect(null)}>
                  Clear Date Selection
                </button>
                <span>Selected Date: {selectedDate}</span>
              </div>
            )}
            <select onChange={(e) => handleDaySelect(e.target.value)}>
              <option value="">Select Date</option>
              {tableData.map((row, index) => (
                <option key={index} value={row[0]}>
                  {row[0]}
                </option>
              ))}
            </select>
          </div>
          <div
            style={{
              width: "90%",
              height: "345px",
              backgroundColor: "#fff",
              margin: "0 auto",
            }}
          >
            <Chart
              data={[
                {
                  label: "Average Minutes per Hour",
                  data: generateChartData(),
                },
              ]}
              axes={[
                { primary: true, type: "linear", position: "bottom" },
                { type: "linear", position: "left" },
              ]}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default WelcomeBody;
