import React, { useState } from "react";
import iotLogo from "../designFIles/IoT_logo_transparent_bigger.png";
import "./welcomeBody.css";

export const WelcomeBody = () => {
  const [showContent, setShowContent] = useState("welcome");

  return (
    <div className="header-style">
      <div className="nav-bar">
        <div className="nav">
          <div className="left-nav-text">IoT Light</div>
          <img
            className="header-logo"
            src={iotLogo}
            alt="IoT Logo"
            style={{ width: "5rem", height: "auto", paddingLeft: "0.5rem" }}
          />
          <div className="right-nav-text">Log Out</div>
        </div>
      </div>
      <div className="menu-layout">
        <div className="parent">
          {<OrderConfirmation />}
          <div>
            <a
              className={
                showVisualMenuSelected === "meniulZilei"
                  ? "clicked-menu"
                  : "child"
              }
              onClick={() => setShowVisualMenuSelected("meniulZilei")}
            >
              Meniul zilei
            </a>
            <a
              className={
                showVisualMenuSelected === "meniulStandard"
                  ? "clicked-menu"
                  : "child"
              }
              onClick={() => setShowVisualMenuSelected("meniulStandard")}
            >
              Meniul standard
            </a>
          </div>
          {isUserAdmin ? (
            <div className="add-delete">
              <button
                className="add"
                style={{ marginRight: "0.3rem" }}
                onClick={() => {
                  openModal();
                  setAddModal(true);
                }}
              >
                Adaugă
              </button>
              <button
                className="delete"
                style={{ marginLeft: "0.1rem" }}
                onClick={handleDelete}
              >
                Șterge
              </button>
            </div>
          ) : isUPT ? (
            <button
              style={{
                backgroundColor: "rgb(10, 236, 175)",
                fontWeight: "bold",
                borderRadius: "10%",
                borderColor: "transparent",
                padding: "0.5rem",
                marginTop: "0.5rem",
              }}
              onClick={() => {
                handleOrder();
              }}
            >
              Rezervă!
            </button>
          ) : (
            <div></div>
          )}{" "}
          {handleOrderRefresh}
        </div>
        {handleOrderRefresh}
        <div className="menu-data" ref={menuDataRef}>
          <ShowMenuCards
            showVisualMenuSelected={showVisualMenuSelected}
            menuDataDaily={menuDataDaily}
            menuDataStandard={menuDataStandard}
            isUPT={isUPT}
            isUserAdmin={isUserAdmin}
            setSelectedCardsPrimaryKey={setSelectedCardsPrimaryKey}
            updateFlagRefresh={setUpdateFlag}
            orderedFoodButtonPressed={orderedFoodButtonPressed}
          />
        </div>
        <Modal
          isModalOpen={isModalOpen}
          useAddModal={useAddModal}
          useDeleteModal={useDeleteModal}
          closeModal={closeModal}
          handleSaveClick={handleSaveClick}
          menuType={showVisualMenuSelected}
          setUpdateFlag={setUpdateFlag}
        />
      </div>
    </div>
  );
};
