import React, { useState } from "react";
import iotLogo from "../designFIles/IoT_logo_transparent_bigger.png";
import "./welcomeBody.css";
import { signOut } from "./firebase"; // Assuming you have a signOut function

export const WelcomeBody = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(true); // Assume user is logged in initially

  const handleLogout = () => {
    signOut(); // Perform sign out action (clear session, etc.)
    setIsLoggedIn(false); // Update authentication status
    window.location.href = "http://localhost:3000/login";
  };

  return (
    <div>
      <div className="header-style">
        <div className="nav">
          <div className="left-nav-text">Eco Light</div>
          <img
            className="header-logo"
            src={iotLogo}
            alt="IoT Logo"
            style={{ width: "5rem", height: "auto", paddingLeft: "0.5rem" }}
          />
          {isLoggedIn && (
            <div className="right-nav-text" onClick={handleLogout}>
              Log Out
            </div>
          )}
        </div>
      </div>
      <div className="options-layout">{/* Your other content */}</div>
    </div>
  );
};

export default WelcomeBody;
