import { initializeApp } from "firebase/app";
import { getAuth, signInWithPopup, GoogleAuthProvider } from "firebase/auth";
import { getAnalytics } from "firebase/analytics";
import { getStorage, ref, getDownloadURL } from "firebase/storage"; // Add these imports for storage operations

const firebaseConfig = {
  apiKey: "AIzaSyDFVKNGe37kqOBl5QfY4OJAhCFlTcX2wIY",
  authDomain: "iot-project-ef186.firebaseapp.com",
  projectId: "iot-project-ef186",
  storageBucket: "iot-project-ef186.appspot.com",
  messagingSenderId: "981937076954",
  appId: "1:981937076954:web:594361c9fe1a034e2943e3",
  measurementId: "G-RE47RXE6NH",
};

// Initialize Firebase
const firebaseApp = initializeApp(firebaseConfig);

// Initialize Firebase Auth provider
const provider = new GoogleAuthProvider();

// Whenever a user interacts with the provider, we force them to select an account
provider.setCustomParameters({
  prompt: "select_account",
});

// Export Firebase Auth instance
export const auth = getAuth();

// Export Firebase Storage instance
export const storage = getStorage();

// Function to sign in with Google popup
export const signInWithGooglePopup = () => signInWithPopup(auth, provider);

// Function to get download URL of a file from Firebase Storage
export const getFileDownloadURL = async (filePath) => {
  const fileRef = ref(storage, filePath);
  return await getDownloadURL(fileRef);
};

