import React, { useState, useEffect } from "react";
import firebase from "firebase/compat/app";
import "firebase/storage";
import logo from "../designFIles/EcoLight.png";
import googleLoginImage from "../designFIles/googlelogin.png";
import "./logInIndex.css";
import { signInWithGooglePopup } from "./firebase";
import { v4 as uuidv4 } from "uuid";

export const LogInIndex = () => {
  const [bulbOnTime, setBulbOnTime] = useState(null);

  const logGoogleUser = async () => {
    try {
      sessionStorage.clear();
      const response = await signInWithGooglePopup();
      console.log(response);
      const token = uuidv4();
      const baseURL = window.location.origin;
      window.location.href = `${baseURL}/welcome?token=${token}`;
    } catch (error) {
      console.error("Error signing in with Google:", error.message);
    }
  };

  useEffect(() => {
    const fetchBulbOnTime = async () => {
      try {
        const firebaseConfig = {
          apiKey: "AIzaSyB3S6pXq8fZXUKignwhjnmGEqFoZlD6CBE",
          authDomain: "iot-light-127885636351.firebaseapp.com",
          projectId: "iot-light-68724",
          storageBucket: "iot-light-68724.appspot.com",
          messagingSenderId: "127885636351",
          appId: "1:127885636351:web:randomlygeneratedappid",
        };

        if (!firebase.apps.length) {
          firebase.initializeApp(firebaseConfig);
        } else {
          firebase.app();
        }

        const storage = firebase.storage();
        const storageRef = storage.ref();
        const bulbOnTimeRef = storageRef.child("bulbOnTime.txt");

        const url = await bulbOnTimeRef.getDownloadURL();
        const response = await fetch(url);
        const data = await response.text();

        setBulbOnTime(data);
        console.log(data);
      } catch (error) {
        console.error("Error fetching bulbOnTime.txt:", error.message);
      }
    };

    fetchBulbOnTime();
    window.history.replaceState({}, document.title, window.location.pathname);
  }, []);

  return (
    <div className="container">
      <img
        className="logo"
        src={logo}
        alt="Logo"
        style={{ width: "25rem", height: "auto" }}
      />
      <img
        id="google-login-btn"
        src={googleLoginImage}
        alt="Google Login"
        className="google-login-button"
        onClick={(e) => {
          e.preventDefault();
          e.stopPropagation();
          logGoogleUser();
        }}
      />
    </div>
  );
};
