import React, { useEffect } from "react";
import logo from "../designFIles/IoT_Light.jpg";
import googleLoginImage from "../designFIles/googlelogin.png";
import "./logInIndex.css";
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";
import { getAuth, GoogleAuthProvider } from "firebase/auth";

const firebaseConfig = {
  apiKey: "YOUR_API_KEY",
  authDomain: "YOUR_AUTH_DOMAIN",
  projectId: "YOUR_PROJECT_ID",
  storageBucket: "YOUR_STORAGE_BUCKET",
  messagingSenderId: "YOUR_MESSAGING_SENDER_ID",
  appId: "YOUR_APP_ID",
  measurementId: "YOUR_MEASUREMENT_ID",
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const analytics = getAnalytics(app);
const auth = getAuth();

export const LogInIndex = () => {
  useEffect(() => {
    const unregisterAuthObserver = auth.onAuthStateChanged((user) => {
      if (user) {
        // User is signed in, redirect to the home page or do any necessary actions
        console.log("User is signed in:", user);
      } else {
        // User is signed out
        console.log("User is signed out");
      }
    });

    // Cleanup function
    return () => unregisterAuthObserver();
  }, []);

  const handleGoogleLogin = () => {
    const provider = new GoogleAuthProvider();
    auth
      .signInWithPopup(provider)
      .then((result) => {
        // This gives you a Google Access Token
        const credential = result.credential;
        // This gives you the signed-in user
        const user = result.user;
        console.log("Successfully signed in:", user);
      })
      .catch((error) => {
        // Handle errors here
        console.error("Error signing in with Google:", error);
      });
  };

  return (
    <div className="container">
      <img
        className="logo"
        src={logo}
        alt="Logo"
        style={{ width: "25rem", height: "auto" }}
      />
      <button className="google-login-button" onClick={handleGoogleLogin}>
        <img src={googleLoginImage} alt="Google Login" />
      </button>
    </div>
  );
};

