import React, { useEffect } from "react";
import logo from "../designFIles/IoT_Light.jpg";
import googleLoginImage from "../designFIles/googlelogin.png";
import "./logInIndex.css";
import firebase from "firebase/app";
import "firebase/auth";
import { initializeApp } from "firebase/app";
import { getAnalytics } from "firebase/analytics";

const firebaseConfig = {
  apiKey: "AIzaSyCJcNhJXQB-pBcmbiGYew8sGC5KW0F-hEY",
  authDomain: "iot-light-a5d96.firebaseapp.com",
  projectId: "iot-light-a5d96",
  storageBucket: "iot-light-a5d96.appspot.com",
  messagingSenderId: "149265343146",
  appId: "1:149265343146:web:89f4c9a95ad24dac1aa6d3",
  measurementId: "G-N026YNGY7Y",
};

// Initialize Firebase
firebase.initializeApp(firebaseConfig);

export const LogInIndex = () => {
  useEffect(() => {
    const unregisterAuthObserver = firebase
      .auth()
      .onAuthStateChanged((user) => {
        if (user) {
          // User is signed in, redirect to the home page or do any necessary actions
          console.log("User is signed in:", user);
        } else {
          // User is signed out
          console.log("User is signed out");
        }
      });

    // Cleanup function
    return () => unregisterAuthObserver();
  }, []);

  const handleGoogleLogin = () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    firebase
      .auth()
      .signInWithPopup(provider)
      .then((result) => {
        // This gives you a Google Access Token
        const credential = result.credential;
        // This gives you the signed-in user
        const user = result.user;
        console.log("Successfully signed in:", user);
      })
      .catch((error) => {
        // Handle errors here
        console.error("Error signing in with Google:", error);
      });
  };

  return (
    <div className="container">
      <img
        className="logo"
        src={logo}
        alt="Logo"
        style={{ width: "25rem", height: "auto" }}
      />
      <button className="google-login-button" onClick={handleGoogleLogin}>
        <img src={googleLoginImage} alt="Google Login" />
      </button>
    </div>
  );
};

